<?phpinclude_once '../config.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST'){    $username = htmlspecialchars($_POST['username']) ;    $pass = $_POST['password'];    $data = [];    $errors = [];    if (empty($username)){        $errors['username'] = 'username is required';    }    if (empty($pass)){        $errors['password'] = 'password is required';    }    if (!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        $data['success'] = true;        // get the password hashed and compare if === to pass        $query = "SELECT * FROM admin WHERE username = ?";        $stmt = $conn->prepare($query);        $stmt->bind_param('s',$username);        $stmt->execute();        $result = $stmt->get_result();        // check if we have result        if ($result->num_rows > 0){            $row = $result->fetch_assoc();            $passHashed = $row['password'];            if (password_verify($pass,$passHashed)){                session_start();                $_SESSION['admin'] = $username;                $data['message'] = 'successfully login';            }else{                $data['message'] = 'Invalid Credentials';                $data['success'] = false;            }        }else{            $data['message'] = 'Invalid Credentials';            $data['success'] = false;        }    }echo json_encode($data);}