<?phpinclude_once '../config.php';global $conn;if(($_SERVER['REQUEST_METHOD'] === 'POST')){    $product = $_POST['product_Id'];    $query = "SELECT * FROM product where product_id = ?";    $stmt = $conn->prepare($query);    $stmt->bind_param('i',$product);    $stmt->execute();    $result = $stmt->get_result();    $row = $result->fetch_assoc();    echo json_encode($row);}