<?phpinclude_once '../config.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST'){    $ProductName = trim($_POST['product_name']);    $product_color = trim($_POST['product_color']);    $unit_price = trim($_POST['unit_price']);    $stack = trim($_POST['stack']);    $branch = trim($_POST['branch']);    $data = [];    $errors = [];    if (empty($ProductName)) {        $errors['productName'] = 'Please provide a valid product name.';    }   if (empty($stack)) {        $errors['stack'] = 'Please provide stack';    }    if (empty($product_color)) {        $errors['color'] = 'Please provide a product color.';    }    if (empty($unit_price)) {        $errors['price'] = 'Please provide the unit price.';    }    if (empty($branch)) {        $errors['branch'] = 'Please specify the branch.';    }    if (!empty($errors)){    $data['success'] = false;    $data['errors'] = $errors;}else{        $data['success'] = true;        $data['message'] = "Success!";        // insert the product in the database        $query = "INSERT INTO product(unit_price, current_stack, branch, product_name, item_color)values(?,?,?,?,?) ";        $stmt = $conn->prepare($query);        $stmt->bind_param('disss',$unit_price,$stack,$branch,$ProductName,$product_color);        if ($stmt->execute()){            $data['message'] = 'New product is successfully created';        }else{            $data['success'] = false;            $data['message'] = 'SQL error'. $stmt->error;        }        $stmt->close();    }echo  json_encode($data);}