<?phpif ($_SERVER['REQUEST_METHOD'] === 'POST'){    $data = [];    $errors = [];    $branch = htmlspecialchars($_POST['branch']);    // validation    if (empty($branch)){        $errors['branch'] = 'please enter a branch!';    }    if (!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        $data['success'] = true;        $data['message'] = 'success';        include_once '../config.php';        global $conn;        // insert branch in to database of branch table        $Query = "INSERT INTO brance(Branch_Name) VALUES (?)";        $stmt = $conn->prepare($Query);        if ($stmt === false){            $data['message'] = 'Error to prepare the Sql Statement';        }else{            $stmt->bind_param('s',$branch);            if ($stmt->execute()){                $data['message'] = 'New Branch is Successfully Created!';            }else{                $data['message'] = $stmt->error;            }        }        $stmt->close();        $conn->close();    }    echo json_encode($data);}