<?phpglobal $conn;include_once '../config.php';if ($_SERVER['REQUEST_METHOD'] === 'POST'){    $data = [];    $errors = [];    $username = htmlspecialchars($_POST['username']);    $branch = htmlspecialchars($_POST['branch']);    $password = $_POST['password'];    $passwordHashed = password_hash($password, PASSWORD_BCRYPT);    if (empty($password)){        $errors['password'] = 'please provide password!';    }    if (empty($username)){        $errors['username'] = 'please provide username!';    }     if (empty($branch)){        $errors['branch'] = 'please provide branch name!';    }if (!empty($errors)){    $data['success'] = false;    $data['errors'] = $errors;}else{    // insert the credential to database    $Query = "insert into employee(username, password, Branch,is_deleted_employee)values(?,?,?,'active')";    $stmt = $conn->prepare($Query);    $stmt->bind_param('sss',$username,$passwordHashed,$branch);    if ($stmt->execute()){        $data['success'] = true;        $data['message'] = 'new user is successfully created!';    }else{        $data['message'] = 'SQL problem' .mysqli_error($conn);    }}echo json_encode($data);}