<?phpif ($_SERVER['REQUEST_METHOD'] === "POST"){    include_once '../config.php';    global $conn;    $Id = $_POST['Id'];    if (isset($Id)){ // check if not null the Id variable        // then make a Query to Delete Branch        $Query = "DELETE FROM brance WHERE id = ?";        $stmt = $conn->prepare($Query);        $stmt->bind_param('i',$Id);        if ($stmt->execute()){            echo json_encode("Branch is Successfully Deleted");        }else{            echo json_encode("Query Problem".$stmt->error);        }        $stmt->close();        $conn->close();    }}