<?phpinclude_once '../config.php';global $conn;//logic for Best seller of the month$month = $_POST['month'];$year = $_POST['year'];$Query = "SELECT    s.branch,    p.product_name,    SUM(s.quantity_sold) AS total_quantity_soldFROM    sales s        JOIN    product p ON s.product_id = p.product_idWHERE    MONTH(s.Sales_date) = ?  AND YEAR(s.Sales_date) = ?GROUP BY    s.branch, p.product_idORDER BY    total_quantity_sold DESCLIMIT 1;";$stmt = $conn->prepare($Query);$stmt->bind_param('ii',$month,$year);$stmt->execute();$result = $stmt->get_result();while ($row = $result->fetch_assoc()){    ?>    <table id="example" class="table table-responsive table-transparent">        <thead>        <tr>            <th>Branch Name</th>            <th>Product Name</th>            <th>Total Sold</th>        </tr>        </thead>        <tbody>        <!-- Your table data goes here -->        <tr>            <td><?=$row['branch'] ?></td>            <td><?=$row['product_name'] ?></td>            <td><?=$row['total_quantity_sold'] ?></td>        </tr>        </tbody>    </table><?php}