<?phpinclude_once '../config.php';require('../Vendors/fpdf186/fpdf.php');if (isset($_GET['startDate']) && isset($_GET['endDate'])) {    global $conn;    $startDate = $_GET['startDate'];    $endDate = $_GET['endDate'];    $total = 0;    $Query = "    SELECT        p.product_name,        s.sale_amount,        s.quantity_sold,        s.branch    FROM        product p        JOIN        sales s ON p.product_id = s.product_id    WHERE        s.Sales_date BETWEEN ? AND ?;    ";    $stmt = $conn->prepare($Query);    $stmt->bind_param('ss', $startDate, $endDate);    $stmt->execute();    $result = $stmt->get_result();    // Create new PDF instance    $pdf = new FPDF();    $pdf->AddPage();    // Set title    $pdf->SetFont('Arial', 'B', 16);    $pdf->Cell(0, 10, 'Sales Report', 0, 1, 'C');    // Date range    $pdf->SetFont('Arial', '', 12);    $pdf->Cell(0, 10, 'From: ' . $startDate . ' To: ' . $endDate, 0, 1, 'C');    // Table header    $pdf->SetFont('Arial', 'B', 12);    $pdf->Cell(50, 10, 'Product Name', 1);    $pdf->Cell(30, 10, 'Branch', 1);    $pdf->Cell(30, 10, 'Quantity Sold', 1);    $pdf->Cell(40, 10, 'Sales Amount', 1);    $pdf->Ln();    // Loop through data and display in PDF    if ($result->num_rows > 0) {        $pdf->SetFont('Arial', '', 12);        while ($row = $result->fetch_assoc()) {            $total += $row['sale_amount'];            $pdf->Cell(50, 10, $row['product_name'], 1);            $pdf->Cell(30, 10, $row['branch'], 1);            $pdf->Cell(30, 10, $row['quantity_sold'], 1);            $pdf->Cell(40, 10, 'PHP: ' . number_format($row['sale_amount'], 2), 1);            $pdf->Ln();        }        $pdf->SetFont('Arial', 'B', 12);        $pdf->Cell(110, 10, 'Total', 1);        $pdf->Cell(40, 10, 'PHP: ' . number_format($total, 2), 1);        $pdf->Ln();    } else {        $pdf->Cell(0, 10, 'No sales data found for the selected date range.', 0, 1, 'C');    }    // Output the PDF    $pdf->Output('D', 'sales_report.pdf');}