<?phpinclude_once '../config.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST'){    $startDate = $_POST['startDate'];    $endDate = $_POST['endDate'];    $total = 0;    $Query = "SELECT    p.product_name,    s.sale_amount,    s.quantity_sold,    s.branchFROM    product p        JOIN    sales s ON p.product_id = s.product_idWHERE    s.Sales_date BETWEEN ? AND ?;";    $stmt = $conn->prepare($Query);    $stmt->bind_param('ss',$startDate,$endDate);    $stmt->execute();    $result = $stmt->get_result();    if ($result->num_rows > 0){       ?>        <tbody>        <?php         while ($row = $result->fetch_assoc()){             $total += $row['sale_amount'];             ?>             <tr>                 <td><?=$row['product_name'] ?></td>                 <td><?=$row['branch'] ?></td>                 <td><?=$row['quantity_sold'] ?></td>                 <td>₱<?= number_format($row['sale_amount'],2) ?></td>             </tr>                 <?php         }        ?>        </tbody>        <tfoot>         <tr>             <td>                 <strong>                     Total: ₱                     <?= number_format($total,2)  ?>                 </strong>             </td>         </tr>        </tfoot>        <?php    }else{        echo "          <div class='alert alert-success' role='alert'>              <h4 class='alert-heading'>No Result Found!</h4>              <p>please try another date!</p>        </div>        ";    }}