<?phpinclude_once '../config.php';global $conn;$StartDate = $_POST['StartDate'];$EndDate = $_POST['EndDate'];$Query = "   SELECT s.EmployeeName,s.branch, p.product_name, SUM(s.quantity_sold) AS total_quantity_soldFROM sales s         JOIN product p ON s.product_id = p.product_idWHERE s.Sales_date BETWEEN ? AND ?GROUP BY s.EmployeeName, p.product_nameORDER BY total_quantity_sold DESC";$stmt = $conn->prepare($Query);$stmt->bind_param('ss',$StartDate,$EndDate);$stmt->execute();$result = $stmt->get_result();// check if we have resultif ($result->num_rows > 0){    while ($row = $result->fetch_assoc()){        ?>        <tr>            <th scope="row"><?=$row['EmployeeName'] ?></th>            <td><?=$row['branch'] ?></td>            <td><?=$row['product_name'] ?></td>            <td><?=$row['total_quantity_sold'] ?></td>        </tr><?php    }}else{    echo "<h5>No result!</h5>";}