<?phpif ($_SERVER['REQUEST_METHOD'] === 'POST'){    $Id = $_POST['Id'];    $branchName = htmlspecialchars($_POST['updateBranch']);    $data = [];    $errors = [];    if (empty($branchName)){        $errors['branchName'] = 'please Enter Branch Name';    }    if (!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        $data['success'] = true;        $data['success'] =  'success!';        if (isset($Id)){            include_once  '../config.php';            global $conn;            $Query = "UPDATE brance SET brance.Branch_Name = ?  WHERE id = ? ";            $stmt = $conn->prepare($Query);            $stmt->bind_param('si',$branchName,$Id);            if ($stmt->execute()){                $data['message'] = 'branch is successfully update';                $data['success'] = true;            }else{                $data['success'] = false;                $errors['errors'] = 'Query Problem';            }        }    }    echo json_encode($data);}