<?phpinclude_once '../config.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === "POST"){    $Id = $_POST['Id'];    $new_stack = $_POST['new_stack'];    $query = "UPDATE product SET current_stack = ? where product_id = ?";    $stmt = $conn->prepare($query);    $stmt->bind_param('ii',$new_stack);    if ($stmt->execute()){        echo  json_encode("successfully");    }else{        echo json_encode('error'.$stmt->error);    }    $stmt->close();    $conn->close();}