<?phpinclude_once '../config.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === "POST"){    $Id = $_POST['Id'];    $query = "SELECT * FROM product WHERE product_id = ?";     $stmt = $conn->prepare($query);     $stmt->bind_param('i',$Id);     $stmt->execute();     $result = $stmt->get_result();     while ($row = $result->fetch_assoc()){         ?>         <!-- Modal -->         <div class="modal fade" id="update_stack_form" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">             <div class="modal-dialog modal-dialog-centered">                 <div class="modal-content">                     <div class="modal-header">                         <h1 class="modal-title fs-5" id="exampleModalLabel"><?= $row['product_name'] ?></h1>                         <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                     </div>                     <div class="modal-body">                        <label for="Quantity">                            <input class="form-control" value="<?=$row['current_stack'] ?>" type="number" min="1" id="update_quantity">                        </label>                     </div>                     <div class="modal-footer">                         <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                         <button style="background-color: #202940" type="button" class="btn text-light">Save</button>                     </div>                 </div>             </div>         </div><?php     }}