       <td><?=$row['product_name'] ?></td>                    <td>                        <?php                        if ($row['current_stack'] <= 1) {                            ?>                            <span class="badge text-bg-danger">(<?=$row['current_stack'] ?> in stock) - High Priority: Restock Soon!</span>                            <?php                        } else {                            ?>                            <span class="badge text-bg-warning">(<?=$row['current_stack'] ?> in stock) - Moderate: Monitor Inventory</span>                            <?php                        }                        ?>                    </td>                    <td><?=$row['item_color'] ?></td>                    <td><?=$row['branch'] ?></td>                    <td>                        <span  class="badge rounded-pill text-bg-primary">                              <button value="<?= $row['product_id'] ?>" id="update_btn" class="btn text-light">                                  <i class="fa-regular fa-pen-to-square"></i>                                  Update                              </button>                        </span>                    </td>                </tr>                <?php            }        } else {            echo "<tr><td colspan='6' class='text-center'>No products found</td></tr>";        }        ?>        </tbody>    </table></main><script>    $(document).ready(function() {        new DataTable('#example', {            paging: false,            scrollCollapse: true,            scrollY: '50vh'        });    });    $(document).ready(function (){        $(document).on('click','#update_btn',function (e){           // create ajax request            $.ajax({                url: '../Models/update_stacks.php',                type: 'POST',                data:{Id: e.target.value},                success:function (res){                    $('#update_stack_form').remove()                    $('body').append(res)                    $('#update_stack_form').modal('show')                }            })        })    })</script><script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js" integrity="sha384-YvpcrYf0tY3lHB60NNkmXc5s9fDVZLESaAA55NDzOxhy9GkcIdslK1eN7N6jIeHz" crossorigin="anonymous"></script><script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.8/dist/umd/popper.min.js" integrity="sha384-I7E8VVD/ismYTF4hNIPjVp/Zjvgyol6VFvRkX/vR+Vc4jQkC+hVqc2pM8ODewa9r" crossorigin="anonymous"></script><script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.min.js" integrity="sha384-0pUGZvbkm6XF6gxjEnlmuGrJXVbNuzT9qBBavbLwCsOGabYfZo0T0to5eqruptLy" crossorigin="anonymous"></script></body></html>