<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>Sales Report</title>    <?php    include_once '../includes/cdn.php';    ?></head><body><header style="background-color: #202940" class="container-fluid text-light p-3 d-flex gap-3">    <nav>        <a href="./dashboard.php">            <i  class="fa-solid text-light fs-1 fa-circle-chevron-left"></i>        </a>    </nav>    <h4>Sales Report</h4></header><div id="con" class="container mt-5 d-flex justify-content-between align-items-center">    <form id="salesReportForm" action="../Models/GenerateSalesReport.php" method="post" class="d-flex gap-3">        <label for="startDate">            <input type="date" class="form-control" id="startDate">        </label>        <label for="EndDate">            <input type="date" class="form-control" id="EndDate">        </label>        <button style="background-color: #202940" type="submit" class="btn text-light fw-bold">Submit</button>    </form>    <div>        <button id="download_pdf" style="background-color: #202940" class="btn fw-bold text-light">            <i class="fa-solid fa-file-pdf"></i>            Download        </button>    </div></div><div class="container mt-5">    <table id="example" class="table display compact cell-border" style="width:100%">        <thead>        <tr>            <th>Product Name</th>            <th>Branch Name</th>            <th>Quantity Sold</th>            <th>Sales Amount</th>        </tr>        </thead>    </table></div><script>// ajax request to display the sales report$(document).ready(function (){$('#salesReportForm').submit(function (event){    event.preventDefault();    const Data = {        startDate: $('#startDate').val(),        endDate: $('#EndDate').val()    }    $.ajax({         url: '../Models/GenerateSalesReport.php',        type: 'POST',        data: Data,        success: function (res,status){            $('#example').html(res);        }})})    // Trigger PDF download on button click    $('#download_pdf').on('click', function() {        const startDate = $('#startDate').val();        const endDate = $('#EndDate').val();        if (startDate && endDate) {            // Open a new window to trigger the PDF generation            window.open('../Models/GenerateReport_PDF.php?startDate=' + startDate + '&endDate=' + endDate, '_blank');        } else {            alert('Please select a date range.');        }    });})</script></body></html>