<?php// if already login prevent to back this login pagesession_start();if (isset($_SESSION['admin'])){    header('Location: ./dashboard.php');    exit();}?><!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>Admin login</title>    <?php    include_once '../includes/cdn.php'    ?>    <style>        body{            font-family: "Matemasie", sans-serif;            font-weight: 400;            font-style: normal;            height: 100vh;            width: 100%;            display: flex;            flex-direction: column;            justify-content: center;            align-items: center;            background-position: center;            background-repeat: no-repeat;            background-size: cover;            background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.5), rgba(16, 12, 12, 0.8)), url('../Assets/bg.jpeg');        }        #logo_img{            width: 100px;            height: 100px;            border-radius: 50%;        }        form{            /* From https://css.glass */            background: rgba(255, 255, 255, 0.08);            border-radius: 16px;            box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);            backdrop-filter: blur(2.3px);            -webkit-backdrop-filter: blur(2.3px);            border: 1px solid rgba(255, 255, 255, 0.31);        }    </style></head><body><div id="message_con"></div><form id="Admin_login_form" class="d-flex flex-column justify-content-center align-items-center p-5 rounded-5" action="#" method="post">    <div>        <img id="logo_img" src="../Assets/capstone_logo.jpg" alt="logo">    </div>    <h1 class="fw-bold text-light">Log In</h1>    <div class="input-group mb-3">        <span class="input-group-text" id="basic-addon1">            <i class="fa-solid fa-user"></i>        </span>        <input id="username" type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1">        <div id="username_msg" class="invalid-feedback"></div>    </div>    <div class="input-group mb-3">        <span class="input-group-text" id="basic-addon1">         <i class="fa-solid fa-lock"></i>        </span>        <input id="password" type="password" class="form-control" placeholder="password" aria-label="password" aria-describedby="basic-addon1">         <div id="password_msg" class="invalid-feedback"></div>    </div>    <div class="input-group mb-3">        <button  style="background-color: #04aa6d; color: #ffffff" type="submit" class="btn fw-bold">Login</button>    </div></form><script>    $(document).ready(function (){        $('#Admin_login_form').submit(function (event){            event.preventDefault();            // data object            const Data ={                username: $('#username').val(),                password: $('#password').val()            }            //  ajax request            $.ajax({                url: '../Auth/Admin_login.php',                type:  'POST',                data: Data,                dataType: 'json',                encode: true,                success: function (res){                    // check if have error                    if (res.errors){                        if (res.errors.username){                            $('#username_msg').html(res.errors.username)                            $('#username').addClass('is-invalid')                        }else {                            $('#username').removeClass('is-invalid')                        }                        if (res.errors.password){                            $('#password_msg').html(res.errors.password)                            $('#password').addClass('is-invalid')                        }else {                            $('#password').removeClass('is-invalid')                        }                    }                    if (res.message){                        if (res.message === 'successfully login'){                            $('#message_con').html(                                `                                 <div id="myToast" class="toast align-items-center text-bg-success border-0 my-4" role="alert" aria-live="assertive" aria-atomic="true">                                <div class="d-flex">                                    <div class="toast-body">                                      <i class="fa-solid fa-circle-exclamation"></i>                                        ${res.message}                                    </div>                                    <button type="button" class="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast" aria-label="Close"></button>                                </div>                              </div>                                `                            )                            var myToust = new bootstrap.Toast('#myToast')                            myToust.show();                            setTimeout(() =>{window.location.href = '../View/dashboard.php'},2000);                        }else {                            $('#message_con').html(                                `                                 <div id="myToast" class="toast align-items-center text-bg-danger border-0 my-4" role="alert" aria-live="assertive" aria-atomic="true">                                <div class="d-flex">                                    <div class="toast-body">                                      <i class="fa-solid fa-circle-exclamation"></i>                                        ${res.message}                                    </div>                                    <button type="button" class="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast" aria-label="Close"></button>                                </div>                              </div>                                `                            )                            var myToust = new bootstrap.Toast('#myToast')                            myToust.show();                        }                    }                }            })        })    })</script><script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js" integrity="sha384-YvpcrYf0tY3lHB60NNkmXc5s9fDVZLESaAA55NDzOxhy9GkcIdslK1eN7N6jIeHz" crossorigin="anonymous"></script></body></html>